var pathTable;
var tableHeight;//当前页面可见区域高度 - 65
var tableWidth;

$(function(){
	//初始化  字典项combobox
	initDictTypeCombobox();
	//初始化  脏器名称combobox
	initDictVisceraCombobox();
	//初始化pathTable
	initPathTable();
	
	$('#layoutMainDiv').css('display','');
	
	//新建\编辑\删除\上移\下移   bind click
	ininClick();
	
	//病理名称    change
	$('#descCn').change(function(){
		$('#py').val(getPYCode(this.value));
	});
	//移动行数
	$('#lineNum').numberbox({
		onChange:function(newValue,oldValue){
			if(!newValue){
				$(this).numberbox('setValue',1);
			}
		}	
	});
});

/**
 * 初始化pathTable
 * @return
 */
function initPathTable(){
	tableHeight = document.body.clientHeight - 65;
	tableWidth  = document.body.clientWidth-2;
	//datagrid 初始化
	pathTable=$('#pathTable').datagrid({
			//url : sybp()+"/dictPathCommonAction_loadList.action",
			title:'',
			iconCls:'',//'icon-save',
			singleSelect:true,//只能选一行
			pagination:false,//下面状态栏
			height:tableHeight,
			width:tableWidth,  
			//fit:true,
			fitColumns:false,//不出现横向滚动条
			nowarp:  false,//单元格里自动换行
			border:false,
			idField:'itemCode', //pk
			toolbar:'#toolbar',
			columns :[[{title :'',field :'itemCode',hidden:true}
			,{title : '',field : 'dictType',width : 50,hidden:true}
			,{title : '',field : 'visceraCode',width : 50,hidden:true}
			,{title : '序号',field : 'sortNum',width : 50,halign:'center',align:'center'
				,formatter: function(value,row,index){
					return index+1;
			}}
			,{title : '名称',field : 'descCn',width : 160,halign:'center'}
			,{field:'py',title:'拼音首字母',width:100,halign:'center',align:'center'}
			,{title : '英文',field : 'descEn',width : 160,halign:'center'}
			,{title : '日文',field : 'descJp',width : 160,halign:'center'}
			,{title : '特殊所见',field : 'specicalFlag',width : 160,halign:'center',align:'center'
				,formatter: function(value,row,index){
					if(value && value == 1){
						return "非常规病变";
					}else if(value && value == 2){
						return "脏器缺失";
					}else{
						return "";
					}
				}
			}
			,{field:'使用频度',title:'freqCount',width:10,hidden:true}
			]],
			onLoadSuccess:function(data){
				var selectId = $('#selectId').val();
				if(selectId){
					$(this).datagrid('selectRecord',selectId);
				}
				$('#editOne').linkbutton('disable');
				$('#delOne').linkbutton('disable');
				$('#upOne').linkbutton('disable');
				$('#downOne').linkbutton('disable');
			},
			onSelect:function(rowIndex, rowData){
				$('#editOne').linkbutton('enable');
				$('#delOne').linkbutton('enable');
				
				var len = $(this).datagrid('getRows').length;
				if(rowIndex == 0){
					$('#upOne').linkbutton('disable');
				}else{
					$('#upOne').linkbutton('enable');
				}
				if(rowIndex == len-1){
					$('#downOne').linkbutton('disable');
				}else{
					$('#downOne').linkbutton('enable');
				}
			}
		});//end datagrid
}

/**
 * 更新datagrid数据
 * @param dictType
 * @param visceraCode
 * @return
 */

function updateDatagrid(dictType,visceraCode){
	//1,3,13,14,15
	if(dictType == 1 || dictType == 3 || dictType == 13 || dictType == 14 || dictType == 15 || dictType == 16){
		$('#pathTable').datagrid({
			url : sybp()+"/dictPathCommonAction_loadList.action?dictType="
				+dictType +"&visceraCode="+visceraCode,
		});
	}else{
		$('#pathTable').datagrid({
			url : sybp()+"/dictPathCommonAction_loadList.action?dictType="
				+dictType ,
		});
	}
	//1,3,13,14,15
	if(dictType == 2 || dictType == 3 ){
		$('#pathTable').datagrid('showColumn','specicalFlag');
	}else{
		$('#pathTable').datagrid('hideColumn','specicalFlag');
	}
}
/**
 * 初始化  字典项combobox
 * @return
 */
function initDictTypeCombobox(){
	$('#dictType').combobox({
		url:sybp()+'/dictPathCommonAction_loadDictType.action',   
	    valueField:'id',    
	    textField:'text',
	    height:19,
	    width:320,
	    editable:false,
	    onChange:function(newValue, oldValue){
			if(newValue == 1 || newValue == 3 ||newValue == 13 || 
					newValue == 14 || newValue == 15 || newValue == 16){
				document.getElementById("dictVisceraSpan").disabled = false;
				$('#dictViscera').combobox('enable');
				
				if($('#dictViscera').combobox('getValue')){
					//新建  可用
					$('#addOne').linkbutton('enable');
					
					//更新datagrid数据
					updateDatagrid(newValue,$('#dictViscera').combobox('getValue'));
				}else{
					//新建  不可用
					$('#addOne').linkbutton('disable');
				}
				
			}else{
				document.getElementById("dictVisceraSpan").disabled = true;
				document.getElementById("dictVisceraSpan").display = '';
				
				$('#dictViscera').combobox('disable');
				//$('#dictViscera').combobox('setValue','');
				//新建  可用
				$('#addOne').linkbutton('enable');
				//更新datagrid数据
				updateDatagrid(newValue);
			}
			
		},
		onLoadSuccess:function(){
			var data = $('#dictType').combobox('getData');
			if(data && data.length){
				$('#dictType').combobox('select',data[0].id);
			}
		}
	});
}
/**
 * 初始化  脏器名称combobox
 * @return
 */
function initDictVisceraCombobox(){
	$('#dictViscera').combobox({
		url:sybp()+'/dictVisceraAction_load1LViscera.action',   
		valueField:'visceraCode',    
		textField: 'visceraName',
		height:19,
		width:160,
		editable:false,
		panelHeight:400,
		onChange:function(newValue, oldValue){
			if(newValue){
				//新建  可用
				$('#addOne').linkbutton('enable');
				
				if($('#dictType').combobox('getValue')){
					//更新datagrid数据
					updateDatagrid($('#dictType').combobox('getValue'),newValue);
				}
			}
		},
		onLoadSuccess:function(){
			var data = $('#dictViscera').combobox('getData');
			if(data && data.length){
				$('#dictViscera').combobox('select',data[0].visceraCode);
			}
		}
	});
}


/**
 * 新建\编辑\删除\上移\下移   bind click
 */
function ininClick(){
	document.getElementById('addOne').onclick = addOne;
	$('#addOne').linkbutton('disable');
	document.getElementById('editOne').onclick = editOne;
	$('#editOne').linkbutton('disable');
	document.getElementById('delOne').onclick = delOne;
	$('#delOne').linkbutton('disable');
	document.getElementById('upOne').onclick = upOne;
	$('#upOne').linkbutton('disable');
	document.getElementById('downOne').onclick = downOne;
	$('#downOne').linkbutton('disable');
}

/**
 * 新建
 * @return
 */
function addOne(){
	showPathAddEditDialog('addOne','新建');
}
/**
 * 编辑
 * @return
 */
function editOne(){
	var row = pathTable.datagrid('getSelected');
	if(row){
		showPathAddEditDialog('editOne','编辑');
	}
}
/**
 * 删除
 * @return
 */
function delOne(){
	var row = pathTable.datagrid('getSelected');
	if(row){
		$.messager.confirm('确认','确定要删除记录吗？',function(r){    
			if (r){    
				delPahtById(row.itemCode);   
			}    
		});
	}
}
/**
 * 删除
 * @param itemCode
 * @return
 */
function delPahtById(itemCode){
	$.ajax({
		url:sybp()+'/dictPathCommonAction_delOne.action?itemCode='+itemCode,
		type:'post',
		dataType:'json',
		success:function(r){
			if(r && r.success){
				//刷新datagrid
				parent.showMessager(1,'删除成功!',true,5000);
				pathTable.datagrid('reload');
			}
		}
	});
}
/**
 * 上移
 * @return
 */
function upOne(){
	var row = pathTable.datagrid('getSelected');
	if(row){
		var rowIndex = pathTable.datagrid('getRowIndex',row);
		var rows = pathTable.datagrid('getRows');
		//移动行数
		var lineNum = $('#lineNum').numberbox('getValue');
		//待移到的序号
		var nextSortNum = 0;
		if(Number(lineNum)<Number(rowIndex)){
			nextSortNum = rows[Number(rowIndex)-Number(lineNum)].sortNum;
		}else{
			nextSortNum = rows[0].sortNum
		}
		$.ajax({
			url:sybp()+'/dictPathCommonAction_upOne.action?sortNum='+row.sortNum
				+'&nextSortNum='+nextSortNum+'&itemCode='+row.itemCode,
			type:'post',
			dataType:'json',
			success:function(r){
				if(r && r.success){
					//刷新datagrid
					//parent.showMessager(1,'上移成功!',true,5000);
					pathTable.datagrid('reload');
				}
			}
		});
	}
}
/**
 * 下移
 * @return
 */
function downOne(){
	var row = pathTable.datagrid('getSelected');
	if(row){
		var rowIndex = pathTable.datagrid('getRowIndex',row);
		var rows = pathTable.datagrid('getRows');
		//移动行数
		var lineNum = $('#lineNum').numberbox('getValue');
		//待移到的序号
		var nextSortNum = 0;
		if(Number(lineNum)+Number(rowIndex) < rows.length){
			nextSortNum = rows[Number(lineNum)+Number(rowIndex)].sortNum;
		}else{
			nextSortNum = rows[rows.length-1].sortNum
		}
		$.ajax({
			url:sybp()+'/dictPathCommonAction_downOne.action?sortNum='+row.sortNum
				+'&nextSortNum='+nextSortNum+'&itemCode='+row.itemCode,
			type:'post',
			dataType:'json',
			success:function(r){
				if(r && r.success){
					//刷新datagrid
					//parent.showMessager(1,'下移成功!',true,5000);
					pathTable.datagrid('reload');
				}
			}
		});
	}
}


/**显示Dialog*/
function showPathAddEditDialog(addOrEdit,title){
	/*签名Dialog*/
	document.getElementById("pathAddEditDialog2").style.display="block";
	$('#addOrEdit').val(addOrEdit);
	//初始化Dialog 显示数据
	initDialogData(addOrEdit);
	$('#pathAddEditDialog').dialog('setTitle',title);
	$('#pathAddEditDialog').dialog('open'); 
	
}


/**
 * 初始化Dialog 显示数据
 * @param addOrEdit
 * @return
 */
function initDialogData(addOrEdit){
	//新建
	if(addOrEdit == 'addOne'){
		
		var dictTypeValue = $('#dictType').combobox('getValue');
		var dictTypeText = $('#dictType').combobox('getText');
		$('#itemCode').val('');
		$('#dictType2').val(dictTypeValue);
		document.getElementById('dictTypeName').firstChild.nodeValue = dictTypeText;
		
		// 1,3,13,14,15 显示脏器名称
		if(dictTypeValue == 1 || dictTypeValue == 3 || dictTypeValue == 13
				|| dictTypeValue == 14 || dictTypeValue == 15 || dictTypeValue == 16){
			//脏器编号,名称
			var visceraCode = $('#dictViscera').combobox('getValue');
			var visceraName = $('#dictViscera').combobox('getText');
			$('#visceraCode').val(visceraCode);
			document.getElementById('visceraNameTR').style.display = '';
			document.getElementById('visceraName').firstChild.nodeValue = visceraName;
		}else{
			document.getElementById('visceraNameTR').style.display = "none";
			$('#visceraCode').val('');
		}
		//2,3 显示  是否特殊所见
		if(dictTypeValue == 2 || dictTypeValue == 3 ){
			document.getElementById('specicalFlagTR').style.display = '';
		}else{
			document.getElementById('specicalFlagTR').style.display = 'none';
		}
		
		$('#oldDescCn').val('');
		$('#descCn').val('');
		$('#py').val('');
		$('#descEn').val('');
		$('#descJp').val('');
		$('#specicalFlag').combobox('select','0');
		
		//显示连续录入复选框
		document.getElementById("continueAddCheckBox").style.display="";
		document.getElementById("continueAddA").style.display="";
		document.getElementById("continueAddCheckBox").checked = false;
		
	}else if(addOrEdit == 'editOne'){
		var row = pathTable.datagrid('getSelected');

		var dictTypeValue = $('#dictType').combobox('getValue');
		var dictTypeText = $('#dictType').combobox('getText');
		$('#itemCode').val(row.itemCode);
		$('#dictType2').val(dictTypeValue);
		document.getElementById('dictTypeName').firstChild.nodeValue = dictTypeText;
		
		// 1,3,13,14,15 显示脏器名称
		if(dictTypeValue == 1 || dictTypeValue == 3 || dictTypeValue == 13
				|| dictTypeValue == 14 || dictTypeValue == 15 || dictTypeValue == 16){
			//脏器编号,名称
			var visceraCode = $('#dictViscera').combobox('getValue');
			var visceraName = $('#dictViscera').combobox('getText');
			$('#visceraCode').val(visceraCode);
			document.getElementById('visceraNameTR').style.display = '';
			document.getElementById('visceraName').firstChild.nodeValue = visceraName;
		}else{
			document.getElementById('visceraNameTR').style.display = "none";
			$('#visceraCode').val('');
		}
		//2,3 显示  是否特殊所见
		if(dictTypeValue == 2 || dictTypeValue == 3 ){
			document.getElementById('specicalFlagTR').style.display = '';
		}else{
			document.getElementById('specicalFlagTR').style.display = 'none';
		}
		
		$('#oldDescCn').val(row.descCn);
		$('#descCn').val(row.descCn);
		$('#py').val(row.py);
		$('#descEn').val(row.descEn);
		$('#descJp').val(row.descJp);
		$('#specicalFlag').combobox('select',row.specicalFlag);
		
		//不显示连续录入复选框
		document.getElementById("continueAddCheckBox").style.display="none";
		document.getElementById("continueAddA").style.display="none";
	}
}


/**
 * 确定
 * @return
 */
function onConfirmButton(){
	//确定   设为不可用
	//$('#confirmButton').linkbutton('disable');
	if($('#pathAddEditForm').form('validate')){
		var addOrEdit = $('#addOrEdit').val();
		//新建
		if(addOrEdit.trim() == "addOne"){
			$.ajax({
				url:sybp()+'/dictPathCommonAction_addOne.action',
				type:'post',
				data:$('#pathAddEditForm').serialize(),
				dataType:'json',
				success:function(r){
					//$('#confirmButton').linkbutton('enable');
					if(r && r.success){
						//刷新treegrid
						parent.showMessager(1,'新建成功!',true,5000);
						$('#selectId').val(r.msg);
						pathTable.datagrid('reload');
						$('#pathAddEditDialog').dialog('close');
						if(document.getElementById("continueAddCheckBox").checked){
							showPathAddEditDialog('addOne','新建');
							document.getElementById("continueAddCheckBox").checked = true;
						}
					}else{
						$.messager.alert('警告','参数交互错误!');
					}
				}
			});
		}else if(addOrEdit.trim() == "editOne"){
			$.ajax({
				url:sybp()+'/dictPathCommonAction_editOne.action',
				type:'post',
				data:$('#pathAddEditForm').serialize(),
				dataType:'json',
				success:function(r){
					//$('#confirmButton').linkbutton('enable');
					if(r && r.success){
						//刷新 datagrid
						parent.showMessager(1,'编辑成功!',true,5000);
						$('#selectId').val(r.msg);
						pathTable.datagrid('reload');
						$('#pathAddEditDialog').dialog('close');
					}else{
						$.messager.alert('警告','参数交互错误!');
					}
				}
			});
		}
	}
	//确定   设为可用
	//$('#confirmButton').linkbutton('enable');
}





